using namespace std;

// 矩阵结构体
struct Matrix {
    int row;
    int col;
};

// 矩阵链相乘
int matrixChainMultiply(vector<Matrix> &matrixs) {
    int n = matrixs.size();
    // 初始化矩阵链乘法结果矩阵
    int dp[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[i][j] = 0;
        }
    }
    // 计算矩阵链乘法结果
    for (int len = 2; len <= n; len++) {
        for (int i = 0; i <= n - len; i++) {
            int j = i + len - 1;
            dp[i][j] = INT_MAX;
            for (int k = i; k < j; k++) {
                int temp = dp[i][k] + dp[k + 1][j] + matrixs[i].row * matrixs[k].col * matrixs[j].col;
                if (temp < dp[i][j]) {
                    dp[i][j] = temp;
                }
            }
        }
    }
    return dp[0][n - 1];
}

int main() {
    vector<Matrix> matrixs;
    Matrix m1 = {3, 5};
    Matrix m2 = {5, 10};
    Matrix m3 = {10, 6};
    Matrix m4 = {6, 8};
    matrixs.push_back(m1);
    matrixs.push_back(m2);
    matrixs.push_back(m3);
    matrixs.push_back(m4);
    int res = matrixChainMultiply(matrixs);
    cout << res << endl;
    return 0;
}
